import Header from './Header'
import Instruction from './Instruction'
import Question from './Question'
import React from 'react'
import axios from 'axios'

//import './App.css';

import XMLData from './xml/mc.xml'
import './mcstyle.css'


class App extends React.Component {

  constructor(props){
    super(props)
    this.state = {
      questions: [],
      questionNum: -1,
      questionTexts: [],
      answers: [],
      value: 0,
      selectedValue: -1,
      selectedAnswer: -1,
      pressSubmit: 0
    }
  }

  incrementValue = (value) => {
    this.setState({
      value: this.state.value + value
    })
  }

  setSelectedValue = (value) => {
    this.setState({
      selectedValue: value
    })
  }

  resetSelected = () => {
    this.setState({
      selectedAnswer: -1
    })
  }

  goToNextQuestion = () => { 
    this.setState({
      questionNum : this.state.questionNum + 1,
      selectedAnswer: -1
    })
  }

  goToPrevQuestion = () => { this.setState({
      questionNum : this.state.questionNum - 1, 
      selectedAnswer: -1
    })
  }

  setSelectedAnswer = (answer) => { this.setState({
      selectedAnswer: answer
     })
  }

  componentDidMount(){
    this.fetchQuestionsWithAxios()
  }

  fetchQuestionsWithAxios(){
    axios.get(XMLData,
      {"Content-Type":"application/xml; charset=utf-8"
    }).then((response)=>{
      const parser = new DOMParser()
      var xml = parser.parseFromString(response.data,"text/xml")

      var questions = Array.from(xml.getElementsByTagName("question"))

      var answers = Array.from(xml.getElementsByTagName("answer"))

      this.setState({
          questions: questions,
          answers: answers
      })
    }).catch( e => {
      console.log(e)
    })
  }

  render(){
    console.log( this.state )
    return (
      <div className="App">
        <Header/>
        <div id ="wrapper" data-role="content">
          <Instruction
            questionNum={this.state.questionNum}
            goToNextQuestion={this.goToNextQuestion}/>
          <Question
            questionNum={this.state.questionNum}
            questions={this.state.questions}
            selectedAnswer={this.state.selectedAnswer}
            selectedValue={this.state.selectedValue}
            value={this.state.value}
            choices = {this.state.answers.filter( (_ , index) => {
              return ((this.state.questionNum*4 <= index) && (index <= this.state.questionNum*4 + 3))
            })}
            goToNextQuestion={this.goToNextQuestion}
            goToPrevQuestion={this.goToPrevQuestion}
            setSelectedAnswer={this.setSelectedAnswer}
            resetSelected={this.resetSelected}
            incrementValue={this.incrementValue}
            setSelectedValue={this.setSelectedValue}

            state={this.state}

          />
        </div>
      </div>
    )
  }
}

export default App;
